let t = 0;
let sh;

function setup() {
  // Set up canvas to fill the entire window and use WEBGL mode
  createCanvas(windowWidth, windowHeight, WEBGL);
  colorMode(RGB, 1, 1, 1, 1);

  // Initialize the shader with vertex and fragment shaders
  sh = createShader(vertexShaderSource, fragmentShaderSource);
  shader(sh);
}

function draw() {
  t += 0.00005 * deltaTime;

  // Update shader uniforms
  sh.setUniform("t", t % 1);
  sh.setUniform("l", t | 0);
  sh.setUniform("aspectRatio", width / height);

  // Draw a rectangle that covers the entire canvas
  rect(-width / 2, -height / 2, width, height);
}

function windowResized() {
  // Adjust the canvas size when the window is resized
  resizeCanvas(windowWidth, windowHeight);
}

// Vertex shader code
const vertexShaderSource = `
attribute vec3 aPosition;
varying vec2 uv;
void main() {
  gl_Position = vec4(aPosition * 2.0 - 1.0, 1.0);
  uv = (vec2(aPosition.x, 1.0 - aPosition.y) + 1.0) * 0.5;
}
`;

// Fragment shader code
const fragmentShaderSource = `
precision highp float;
#define PI ` + Math.PI + `
varying vec2 uv;
uniform float t, l;
uniform float aspectRatio;

mat2 rot(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float map(vec3 p) {
  p.xy *= p.z * mix(1.0, 2.0, (mod(l, 2.0) == 0.0) ? t : 1.0 - t);
  p.xz *= rot(p.y * mix(.2, .8, sin(t * PI * 2.) * .5 + .5)) * rot(t * PI * 4.);
  p.z -= 5.0;
  return length(p) - .5 + sin(p.x) * sin(p.y) * sin(p.z * .5 * sin(t * PI * 2.) * .5 + .5) * .75;
}

vec3 norm(vec3 p) {
  vec2 e = vec2(0.0001, 0.);
  return normalize(map(p) - vec3(map(p - e.xyy), map(p - e.yxy), map(p - e.yyx)));
}

void main() {
  vec2 adjustedUV = uv * vec2(aspectRatio, 1.0);
  vec3 n = norm(vec3(adjustedUV, 0) + vec3(0, 0, 1) * map(vec3(adjustedUV, 0)));
  gl_FragColor = vec4(vec3(.22, 0., .6) * n.x + vec3(1., .33, 0.) * (mix(n.x, n.y, sin(smoothstep(0., 1., t) * PI * 2.) * .5 + .5)) + vec3(1., .75, 0.) * mix(n.z, n.y, sin(smoothstep(0., 1., t) * PI * 2.) * .5 + .5), 1);
}
`;
