let _nsRate;
let _maxPoint;
let _aryObject = [];
let _objectNum;
let _bgColor;

function setup() {
  createCanvas(windowWidth, windowHeight);
  frameRate(30);
  colorMode(HSB, 360, 100, 100, 255);
  noFill();
  drawingContext.shadowColor = color(0);
  drawingContext.shadowOffsetY = width/20;
  drawingContext.shadowBlur = width/5;
  _objectNum = 30;
  _nsRate = 0.002;
  _maxPoint = 10;
  let nsX = random(10);
  let nsY = random(10);
  let sw = random(width/2, width/4)*4;
  for (let i = 0; i < _objectNum; i++) {
    let col = color(random(360), 80, 80, 255);
    _aryObject.push(new line(nsX-i/10, nsY-i/10, col, sw-sw/_objectNum*i));
  }
  _bgColor = color(0);
}

function draw() {
  background(0);
  for (let i = 0; i < _objectNum; i++) {
    _aryObject[i].update();
    _aryObject[i].draw();
  }
}

class line {
  constructor(nsX, nsY, color, sw) {
    this.nsX = nsX;
    this.nsY = nsY;
    this.color = color;
    this.sw = sw;
    this.aryPoints = [];
  }
  update() {
    this.nsX += _nsRate;
    this.nsY += _nsRate;
    this.aryPoints.unshift([
      width/3 * cos(8*PI*noise(this.nsX)),
      height/3 * sin(8*PI*noise(this.nsY))
    ]);
    while (this.aryPoints.length > _maxPoint) {
      this.aryPoints.pop();
    }
  }
  draw() {
    stroke(this.color);
    strokeWeight(this.sw);
    push();
    translate(width/2, height/2);
    beginShape();
    for (let i = 0; i < this.aryPoints.length; i++) {
      curveVertex(this.aryPoints[i][0], this.aryPoints[i][1]);
    }
    endShape();
    pop();
  }
}

